# -*- coding: utf-8 -*-  import pytest  import sys   from solucion import procesar_cadena # Importación limpia y directa.    # =================================================================  # ⭐ PRUEBAS LÓGICAS ⭐  # =================================================================    def test_caso_base_longitud_par():      """Verifica la inversión y conteo con una cadena de longitud par."""      cadena = "Hola"      resultado_esperado = ("aloH", 2)      resultado_obtenido = procesar_cadena(cadena)            assert resultado_obtenido == resultado_esperado, f"Error en el caso base. Esperado: {resultado_esperado}, Obtenido: {resultado_obtenido}"    def test_cadenas_con_mayusculas_y_minusculas():      """Verifica que cuente correctamente las vocales en mayúsculas y minúsculas."""      cadena = "Algoritmo"      resultado_esperado = ("omtiroglA", 4)      resultado_obtenido = procesar_cadena(cadena)            assert resultado_obtenido == resultado_esperado, f"Error en conteo de mayúsculas/minúsculas. Esperado: {resultado_esperado}, Obtenido: {resultado_obtenido}"    def test_cadenas_vacias():      """Verifica el comportamiento con una cadena vacía."""      cadena = ""      resultado_esperado = ("", 0)      resultado_obtenido = procesar_cadena(cadena)            assert resultado_obtenido == resultado_esperado, f"Error con cadena vacía. Esperado: {resultado_esperado}, Obtenido: {resultado_obtenido}"    def test_solo_vocales():      """Verifica el caso de una cadena que solo tiene vocales."""      cadena = "AEIOUaeiou"      resultado_esperado = ("uoieaUOIEA", 10)      resultado_obtenido = procesar_cadena(cadena)            assert resultado_obtenido[1] == 10, f"Error: Falló el conteo de vocales. Se esperaba 10, se obtuvo {resultado_obtenido[1]}"      assert resultado_obtenido[0] == resultado_esperado[0], f"Error: Falló la inversión de la cadena. Esperado: '{resultado_esperado[0]}', Obtenido: '{resultado_obtenido[0]}'"    def test_cadenas_con_espacios_y_numeros():      """Verifica el comportamiento con espacios y caracteres no alfabéticos."""      cadena = "123 a e i"      resultado_esperado = ("i e a 321", 3)      resultado_obtenido = procesar_cadena(cadena)            assert resultado_obtenido == resultado_esperado, f"Error: Falló al procesar espacios/números. Esperado: {resultado_esperado}, Obtenido: {resultado_obtenido}"